const ActionNode = require('tree.node.action')

class Suicide extends ActionNode {
  tick (context) {
    if (Game.time + 1 === this.getNodeMemory(context).openTime) {
      return SUCCESS
    }

    if (Game.time > this.getNodeMemory(context).openTime) {
      return FAILURE
    }

    var creep = context.actor
    creep.suicide()

    return RUNNING
  }
}

module.exports = Suicide
