module.exports = {
  /**
   * Determines whether a structure needs repair.
   *
   * @param {Structure} structure
   * @return {Boolean}
   */
  needsRepair: (structure) => structure.hits < structure.hitsMax,

  /**
   * Checks if the room of the two room objects matches
   *
   * @param {RoomObject} roomObjectA
   * @param {RoomObject} roomObjectB
   * @return {Boolean} whether the room objects are in the same room
   */
  sameRoom: (roomObjectA, roomObjectB) => roomObjectA.room === roomObjectB.room,

  /**
   * Checks if the room of the two room objects matches
   *
   * @param {RoomObject} roomObject
   * @param {Room} room
   * @return {Boolean} whether the room objects are in the same room
   */
  inRoom: (roomObject, room) => roomObject.room === room,

  /**
   * Checks whether a spawn is busy (spawning)
   *
   * @param {Spawn} spawn
   * @return {Boolean} whether the spawn is busy
   */
  spawnIsBusy: (spawn) => spawn.spawning !== null
}
