module.exports = {
  /** @param {Creep} creep **/
  run: function(creep) {
    creep.say('CLAIM!');
    if (typeof Game.flags.NextRoom === 'undefined') {
      return;
    }

    /** @var {Flag} flag */
    var flag = Game.flags.NextRoom;
    creep.moveTo(flag);
    var controller = creep.pos.findClosestByRange(STRUCTURE_CONTROLLER);

    var claimResult= creep.claimController(controller);

    switch(claimResult) {
      case ERR_GCL_NOT_ENOUGH:
        console.log('Global control level not enough');
        break;
      default:
        console.log(claimResult);
    }

  }
};