module.exports = {

  spawn: function(desired) {
    //todo
  },
  /** @param {Creep} creep **/
  run: function (creep) {

    if (typeof creep.memory.upgrading === 'undefined') {
      creep.memory.upgrading = false;
    }

    if (creep.carry.energy === 0) {
      creep.memory.upgrading = false;
    }

    if (creep.carry.energy === creep.carryCapacity) {
      creep.memory.upgrading = true;
    }

    if (creep.memory.upgrading) {
      if (creep.upgradeController(creep.room.controller) === ERR_NOT_IN_RANGE) {
        creep.moveTo(creep.room.controller);
      }
      return;
    }


    var spawnPoint =  Game.spawns[Object.keys(Game.spawns)[0]];


    if (spawnPoint.transferEnergy(creep,creep.energyCapacity-creep.carry.energy) === ERR_NOT_IN_RANGE) {
      creep.moveTo(spawnPoint);
    }
  }
};