var specs = require('specs')

/**
 * Basic Room Layout
 *
 * @param {Room} room
 */
module.exports = (room) => {
  specs(room, {
    switch: [
      {
        layout: 'basic',
        condition: (room) => room.controller.level === 1
      },
      {
        layout: 'basic3',
        condition: (room) => room.controller.level === 3
      }
    ],
    spawn: [
      {role: 'harvester', count: 1},
      {role: 'harvester', count: 2},
      {role: 'upgrader', count: 1},
      {role: 'upgrader', count: 2},
      {role: 'builder', count: 1},
      {role: 'builder', count: 2}
    ]
    // TODO: build up to 5 extensions
    // TODO: build roads between sources and controller/spawn
    // TODO: build roads from spawn to each neighbour room
    // TODO: build wall leaving space for rampart on the road
    // TODO: build ramparts
    // TODO: build 1-5 containers ?
  })
}
