var specs = require('specs')

/**
 * Basic Room Layout
 *
 * @param {Room} room
 */
module.exports = (room) => {
  specs(room, {
    switch: [
      {
        layout: 'basic2',
        condition: (room) => room.controller.level === 2
      }
    ],
    spawn: [
      {role: 'harvester', count: 1},
      {role: 'harvester', count: 2},
      {role: 'upgrader', count: 1},
      {role: 'upgrader', count: 2},
      {role: 'upgrader', count: 3},
      {role: 'upgrader', count: 4},
      {role: 'builder', count: 1},
      {role: 'builder', count: 2}
    ],
    tower: {}
    // TODO: tower logic
    // TODO: build up to 10 extensions
    // TODO: build tower
  })
}
