module.exports = {
  /**
   * Compare function for structures according their absolute health
   *
   * @param {Structure} structureA
   * @param {Structure} structureB
   * @return {Number} sort key
   */
  structuresByHits: (structureA, structureB) => structureB.hits - structureA.hits,

  /**
   * Compare function for structures according their relative health
   *
   * @param {Structure} structureA
   * @param {Structure} structureB
   * @return {Number} sort key
   */
  structureByHitsPercentage: (structureA, structureB) =>
  structureB.hits / structureB.hitsMax - structureA.hits / structureA.hitsMax
}
