/**
 *
 * @param {Room} room
 * @param {Object[]} options
 * @param {string} options.layout
 * @param {function} options.condition
 * @returns {Boolean}
 */
module.exports = (room, options) => {
  return _.reduce(options, (check) => {
    if (check.condition(room)) {
      return true
    }
  }, false)
}
