const CompositeNode = require('tree.node.composite')

class Priority extends CompositeNode {
  tick (context) {
    super.tick(context)

    var status = FAILURE
    this.nodes.forEach((node) => {
      if (status !== FAILURE) {
        return
      }

      status = node.execute(context)
    })
    return status
  }
}

module.exports = Priority
