const CompositeNode = require('tree.node.composite')

class Sequence extends CompositeNode {
  tick (context) {
    super.tick(context)

    var status = SUCCESS
    this.nodes.forEach((node) => {
      if (status !== SUCCESS) {
        return
      }

      status = node.execute(context)
    })
    return status
  }
}

module.exports = Sequence
